pragma solidity ^0.4.0;
pragma experimental ABIEncoderV2;

contract AppleFactory {

    address[] addresses;

    function create(uint weight, string species) {
        address newContract = new AppleContract(weight, species);
        addresses.push(newContract);
    }

}

contract AppleContract {

    Apple public apple;

    struct Apple {
        uint weight;
        string species;
    }

    function AppleContract(uint weight, string species) {
        apple = Apple(weight, species);
    }

}
